mapper.select:
select q.id, q.log_id,q.data_id,q.status,q.error,t.type_id
from job_detail q,job_log t
where t.id=q.log_id and q.status = 0 limit #{pageSize}

select q.id, q.log_id,q.data_id,q.status,q.error,t.type_id
from job_detail q,job_log t
where t.id=q.log_id and t.type_id=#{typeId} and a.id>#{id}

select count(*) from job_detail

     and log_id=#{logId}
     and status = -1

select q.id,q.data_id,q.status,q.error,q.last_time,t.type_id,p.name from job_detail q,job_log t,job_type p where t.id=q.log_id and p.id = t.type_id
and q.log_id=#{logId} and q.status = -1
order by q.last_time desc, q.id desc limit #{offset},#{pageSize}
